<?phpheader('Content-Type: application/rss+xml; charset=utf-8');// includesinclude("admin/cms/configuration.php");include("php/func_all_html_entities_decode.php");include("admin/cms/dataaccess.php");// db-anfragen$cont_tmp = select_desc("adhs_cont", "*", "", "", "id");$i = 0;$cont = array();foreach($cont_tmp as $con_tmp) {    if($con_tmp["icon"] == "0" && $con_tmp["pubdate"] != "0000-00-00" && $con_tmp["hidden"] != "1" && $i < 20) {        $pubdate_ts = strtotime ($con_tmp["pubdate"]);        while (array_key_exists($pubdate_ts, $cont)):            $pubdate_ts++;        endwhile;        $cont[$pubdate_ts] = $con_tmp;        $i++;    }}krsort($cont);// headecho'<?xml version="1.0" encoding="utf-8"?><rss version="2.0"><channel><title>AD(H)S Studien</title><link>https://www.adhs-studien.info'.$_SERVER['PHP_SELF'].'</link><description>Wissenschaftliche Informationen zu ADHS bei Kindern und Erwachsenen</description><image><url>https://www.adhs-studien.info/img/banner2.png</url><title>AD(H)S Studien</title><link>https://www.adhs-studien.info'.$_SERVER['PHP_SELF'].'</link></image><language>de-de</language><atom10:link xmlns:atom10="http://www.w3.org/2005/Atom" rel="self" type="application/rss+xml" href="https://www.adhs-studien.info'.$_SERVER['PHP_SELF'].'" />';foreach($cont as $con) {	$datum = $con["pubdate"];	$icon = $con["icon"];    // ------------------------    $einzelansicht = array_pop( explode( "/", $con["link"] ) );    $einzelansicht_link = array_shift( explode( ".", $einzelansicht ) );    $link = 'https://www.adhs-studien.info/'.$einzelansicht_link.'.html';	if($con["titel"] != '') {		$titel = umlautewegmachen_rss($con["titel"]);		$text = umlautewegmachen_rss($con["text"]);    echo'    <item>        <title>'.$titel.'</title>        <link>'.$link.'</link>        <description xml:space="preserve"><![CDATA['.$text.']]></description>        <guid>'.utf8_encode($link).'</guid>        <pubDate>'.$datum.'</pubDate>    </item>';		}	if($con["titel_en"] != '') {		$titel = umlautewegmachen_rss($con["titel_en"]);		$text = umlautewegmachen_rss($con["text_en"]);		echo'	    <item>	        <title>'.$titel.'</title>	        <link>'.$link.'</link>	        <description xml:space="preserve"><![CDATA['.$text.']]></description>	        <guid>'.utf8_encode($link).'</guid>	        <pubDate>'.$datum.'</pubDate>	    </item>';		}}echo'</channel></rss>';?>