<?phpheader('Content-Type: text/xml; charset=utf-8');echo '<?xml version="1.0" encoding="UTF-8"?>';echo '<urlset xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance"         xsi:schemaLocation="http://www.sitemaps.org/schemas/sitemap/0.9 http://www.sitemaps.org/schemas/sitemap/0.9/sitemap.xsd"         xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">';// includesinclude("admin/cms/configuration.php");include("admin/cms/dataaccess.php");#include("admin/cms/funktionen.inc.php");  -------------$navi = select("adhs_kat", "*", "", "", "id");$cont = select("adhs_cont", "*", "", "", "id");foreach ($navi as $nav) {    $link_title = _convert($nav["link_title"]);    $hidden = $nav["hidden"];    if ($hidden == "0") {        if ($nav["titel"] != "" || $nav["titel_en"] != "") {            echo '            <url>                <loc>https://www.adhs-studien.info/' . $link_title . '.html</loc>                <lastmod>' . date("Y-m-d") . '</lastmod>                <changefreq>always</changefreq>                <priority>1.0</priority>            </url>';        }    }}foreach ($cont as $con) {    if ($con["icon"] == "0" && $con["hidden"] == "0") { # nur dokumente ver√∂ffentlichen        $einzelansicht = array_pop(explode("/", _convert($con["link"])));        $einzelansicht_link = array_shift(explode(".", $einzelansicht));        $lastmod = strtotime($con["lastmod"]);        if ($con["titel"] != "" || $con["titel_en"] != "") {            echo '<url>';            echo '  <loc>https://www.adhs-studien.info/' . $einzelansicht_link . '.html</loc>';            echo '  <lastmod>' . date("Y-m-d", $lastmod) . '</lastmod>';            echo '  <changefreq>weekly</changefreq>';			echo '  <priority>0.8</priority>';			echo '</url>';        }    }}echo '</urlset>';?>